mapper.update:

UPDATE SNSBOARD
SET SNS_HIDE = '1'
WHERE SNS_NUMBER = #{SNS_NUM}


UPDATE SNSBOARD
SET SNS_HIDE = '0'
WHERE SNS_NUMBER = #{SNS_NUMBER}


UPDATE SNSBOARD
SET SNS_LIKE = SNS_LIKE+1
WHERE SNS_NUMBER = #{SNS_NUMBER}


UPDATE SNSBOARD
SET SNS_LIKE = SNS_LIKE-1
WHERE SNS_NUMBER = #{SNS_NUMBER}

mapper.select:

SELECT MEMBER_NUMBER
FROM SNSBOARD
WHERE SNS_NUMBER = #{SNS_NUMBER}
