mapper.select:

SELECT MEMBER_NICKNAME
FROM member
WHERE MEMBER_NUMBER = #{MEMBER_NUMBER}


SELECT ORIGINAL_FILE_NAME
FROM member
WHERE MEMBER_NUMBER = #{MEMBER_NUMBER}

mapper.update:

		UPDATE member
        SET ORIGINAL_FILE_NAME = #{ORIGINAL_FILE_NAME},
            STORED_FILE_NAME = #{STORED_FILE_NAME}

        WHERE MEMBER_NUMBER = #{MEMBER_NUMBER}
